package leetcode;

import java.util.*;
import java.lang.Character;


public class lc389_FindTheDifference {
	
	
	public static void runner() {
		
		
		

		String s = "";
		String t = "";
		
		System.out.println(lc389_FindTheDifference.findTheDifference(s, t));
		
		
	}
	
	public static char findTheDifference(String s, String t) {
		
		HashMap<Character, Integer> orig = new HashMap<Character, Integer>();
		
		for(char c : s.toCharArray()) {
			if(orig.containsKey(c)) {
				orig.replace(c, orig.get(c) + 1 ); 
			}else {
				orig.put(c, 1);
			}
		}
		
		for(char c : t.toCharArray()) {
			if(orig.containsKey(c)) {
				
				if(orig.get(c) == 0) {
					return c;
				}else {
					orig.replace(c, orig.get(c) - 1 );
				}
			}else {
				return c;
			}			
		}		
		return '0';
    }
	
	
}
